import React, { useState } from 'react';
import { fetchInfluencerData } from '../services/api';

interface Props {
    onSearch: (data: any) => void;
}

const InfluencerSearch: React.FC<Props> = ({ onSearch }) => {
    const [name, setName] = useState('');

    const handleSearch = async () => {
        const data = await fetchInfluencerData(name);
        onSearch(data);
    };

    return (
        <div>
            <input
                type="text"
                placeholder="Enter influencer handle (e.g., @cristiano)"
                value={name}
                onChange={(e) => setName(e.target.value)}
            />
            <button onClick={handleSearch}>Fetch Data</button>
        </div>
    );
};

export default InfluencerSearch;
